mapper.select:
select

,

,
(SELECT ic.ic_name FROM item_class ic WHERE item.ic_id = ic.ic_id) 'icName'
from item
where 1=1

  and item_id=#{itemId,jdbcType=INTEGER}


  and ic_id=#{icId,jdbcType=VARCHAR}


  and brand_id=#{brandId,jdbcType=INTEGER}


  and type_id=#{typeId,jdbcType=INTEGER}


  and doctor_id=#{doctorId,jdbcType=INTEGER}


  and hospital_id=#{hospitalId,jdbcType=INTEGER}

LIMIT #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT i.item_id 'itemId', i.ic_id 'icId', i.item_name 'itemName', i.subtitle 'subtitle', i.item_image 'itemImage', i.doctor_id 'doctorId', i.hospital_id 'hospitalId', i.type_id 'typeId', i.original_price 'originalPrice', i.selling_price 'sellingPrice', (SELECT d.doctor_name FROM doctor d WHERE d.doctor_id=i.doctor_id) 'doctorName', (SELECT d.doctor_pic FROM doctor d WHERE d.doctor_id=i.doctor_id) 'doctorPic', (SELECT h.hospital_name FROM hospital h WHERE h.hospital_id=i.hospital_id) 'hospitalName', (SELECT d.doctor_title FROM doctor d WHERE d.doctor_id=i.doctor_id) 'doctorTitle', (SELECT ic.ic_name FROM item_class ic WHERE i.ic_id = ic.ic_id) 'icName' FROM item i WHERE 1=1
and i.hospital_id = #{hospitalId,jdbcType=INTEGER}
and i.ic_id = #{icId,jdbcType=INTEGER}
and i.brand_id = #{brandId,jdbcType=INTEGER}
and type_id=#{typeId,jdbcType=INTEGER}
and doctor_id=#{doctorId,jdbcType=INTEGER}
LIMIT #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT c.case_id, c.case_name, c.case_desc, c.case_before_pic, c.case_after_pic FROM `case` c WHERE 1=1
and c.item_id=#{itemId,jdbcType=INTEGER}
and c.hospital_id = #{hospitalId,jdbcType=INTEGER}
LIMIT 0,2
