mapper.select:
select , '' as userHeader, '美人谷' as nickName,
0 AS readNum, 0 AS commentNum, 0 AS collectNum,
(SELECT ac_name FROM article_class ac WHERE ac.ac_id=a.ac_id) AS className
from
 a
where flag=1

    AND article_id=#{id}


    AND ac_id=#{acId}


    AND article_is_banner=#{isBanner}


    AND article_show=#{show}


    AND article_is_commend=#{isCommend}


    AND article_is_publish=#{isPublish}



    order by #{sortBy} #{order}

select count(*)
from

where flag=1

    AND article_id=#{id}


    AND ac_id=#{acId}


    AND article_is_banner=#{isBanner}


    AND article_show=#{show}


    AND article_is_commend=#{isCommend}


    AND article_is_publish=#{isPublish}



    order by #{sortBy} #{order}
