mapper.select:
select a.article_id as id, a.ac_id as acId, a.article_url as url, a.article_show as `show`,
a.article_sort as sort, a.article_img as img, a.article_title as title, a.article_content as content,
a.article_is_banner as isBanner, a.article_is_commend as isCommend, a.article_time as `time`,
a.article_is_publish as isPublish, a.flag , DATE_FORMAT(a.create_time, "%Y-%c-%d %h:%m:%s") as createTime, a.create_user as createUser,
a.create_user_type as createUserType, IFNULL(a.create_user_name, '') as createUserName, IFNULL(a.create_user_img, '') as createUserImg,
a.article_label as labe, ac.ac_name,
0 AS readNum, 0 AS commentNum, 0 AS collectNum,
(SELECT ac_name FROM article_class ac WHERE ac.ac_id=a.ac_id) AS className
from
 a
left JOIN article_class ac ON a.ac_id=ac.ac_id
where a.flag=1

    AND a.article_id=#{id}


    AND a.ac_id=#{acId}


    AND a.article_is_banner=#{isBanner}


    AND a.article_show=#{show}


    AND a.article_is_commend=#{isCommend}


    AND a.article_is_publish=#{isPublish}


    AND a.create_user=#{createUser}
    AND a.create_user_type=#{createUserType}


order by a.article_is_banner desc,a.create_time desc

select count(*)
from
 a
left JOIN article_class ac ON a.ac_id=ac.ac_id
where a.flag=1

    AND a.article_id=#{id}


    AND a.ac_id=#{acId}


    AND a.article_is_banner=#{isBanner}


    AND a.article_show=#{show}


    AND a.article_is_commend=#{isCommend}


    AND a.article_is_publish=#{isPublish}


    AND a.create_user=#{createUser}
    AND a.create_user_type=#{createUserType}



    AND a.create_user=#{createUser}


order by a.article_is_banner desc,a.create_time desc
