mapper.select:
SELECT oi.`item_name` as itemName, d.`doctor_name` as doctorName, d.doctor_id as doctorId, d.cultural_top as culturalTop, h.`hospital_name` as hospitalName, oi.item_price as itemPrice, oi.item_num as itemNum, oi.item_image as itemImage, '' AS appointCode, IFNULL(o.payment_time,'') as paymentTime, o.add_time as createTime, d.mobile as doctorMobile, o.union_sn as unionSN, o.order_sn as orderSN, o.order_id as orderId, o.order_state as orderState, (SELECT GROUP_CONCAT(ic_name SEPARATOR '/') FROM item_class WHERE ic_id IN (i.`ic_id`)) AS itemClass FROM `order` o LEFT JOIN order_item oi ON oi.`order_id`=o.`order_id` LEFT JOIN item i ON i.item_id=oi.`item_id` LEFT JOIN hospital h ON oi.`hospital_id`=h.`hospital_id` LEFT JOIN doctor d ON d.`doctor_id`=i.`doctor_id` WHERE flag=1
AND o.union_sn=#{unionSN}
AND o.order_id=#{id}

mapper.update:
UPDATE `order` SET

    order_state=#{orderState},


    refund_state=#{refundState}

WHERE flag=1 and order_id=#{id};

mapper.select:
SELECT o.`order_state` as orderState, oi.`item_name` as itemName, d.`doctor_name` as doctorName, d.`doctor_title` as doctorTitle, oi.item_num as itemNum, oi.item_price as itemPrice, o.order_sn as orderSN, d.mobile as doctorMobile, IFNULL(o.payment_time, '') as paymentTime, oi.item_image as itemImage, h.hospital_id as hospitalId, i.item_id as itemId, o.union_sn as unionSN, (SELECT GROUP_CONCAT(ic_name SEPARATOR '/') FROM item_class WHERE ic_id IN (i.`ic_id`)) AS itemClass FROM `order` o LEFT JOIN order_item oi ON o.`order_id`=oi.`order_id` LEFT JOIN item i ON i.item_id=oi.`item_id` LEFT JOIN hospital h ON oi.`hospital_id`=h.`hospital_id` LEFT JOIN doctor d ON oi.`doctor_id`=d.`doctor_id` WHERE o.`flag`=1
AND o.`order_state` IN (#{state})
AND o.user_id=#{userId}
ORDER BY o.`add_time` DESC;

SELECT count(o.order_id) FROM `order` o LEFT JOIN order_item oi ON o.`order_id`=oi.`order_id` LEFT JOIN item i ON i.item_id=oi.`item_id` LEFT JOIN hospital h ON oi.`hospital_id`=h.`hospital_id` LEFT JOIN doctor d ON oi.`doctor_id`=d.`doctor_id` WHERE o.`flag`=1
AND o.`order_state` IN (#{state})
AND o.user_id=#{userId}
ORDER BY o.`add_time` DESC;
