mapper.select:
select 
,p.province AS province,c.city AS city,area.area AS areas
from user_address  a
LEFT  JOIN  province p ON  a.province_id = p.provinceID
LEFT  JOIN  city c ON  a.city_id = c.cityID
LEFT  JOIN  area area ON  a.area_id = area.areaID
where 1 = 1  AND  delflag = 0

  AND address_id = #{addressId,jdbcType=INTEGER}


  AND user_id = #{userId,jdbcType=INTEGER}


  AND user_name = #{userName,jdbcType=VARCHAR}


  AND user_phone = #{userPhone,jdbcType=VARCHAR}


  AND user_address = #{userAddress,jdbcType=VARCHAR}


  AND is_default = #{isDefault,jdbcType=BIT}


  AND create_time = #{createTime,jdbcType=TIMESTAMP}


  AND update_time = #{updateTime,jdbcType=TIMESTAMP}

select ,p.province AS province,c.city AS city,area.area AS areas
from
user_address a
LEFT  JOIN  province p ON  a.province_id = p.provinceID
LEFT  JOIN  city c ON  a.city_id = c.cityID
LEFT  JOIN  area area ON  a.area_id = area.areaID
where 1=1

  AND user_id = #{userId,jdbcType=INTEGER}


  AND user_name = #{userName,jdbcType=VARCHAR}


  AND user_phone = #{userPhone,jdbcType=VARCHAR}


  AND  user_address = #{userAddress,jdbcType=VARCHAR}


  AND is_default = #{isDefault,jdbcType=BIT}


  AND create_time = #{createTime,jdbcType=TIMESTAMP}


  AND update_time = #{updateTime,jdbcType=TIMESTAMP}

order by create_time DESC

select count(*)
from
user_address
where 1=1

  AND user_id = #{userId,jdbcType=INTEGER}


  AND user_name = #{userName,jdbcType=VARCHAR}


  AND user_phone = #{userPhone,jdbcType=VARCHAR}


  AND  user_address = #{userAddress,jdbcType=VARCHAR}


  AND is_default = #{isDefault,jdbcType=BIT}


  AND create_time = #{createTime,jdbcType=TIMESTAMP}


  AND update_time = #{updateTime,jdbcType=TIMESTAMP}

order by create_time DESC
