select a.nickname, a.realname, a.phone, a.idcard AS idCard, a.bank_code AS bankCode, a.bank_idcard AS bankIdCard, a.bank_phone AS bankPhone, a.avatar, a.sex, a.birthday, a.email, a.area_id AS areaId, a.register_from AS registerFrom, a.register_time AS registerTime, a.login_time AS loginTime, a.last_login_time AS lastLoginTime, a.login_ip AS loginIp, a.last_login_ip AS lastLoginIp, a.login_from AS loginFrom, a.login_num AS loginNum, a.is_auth AS isAuth, a.is_buy AS isBuy, a.is_allow_inform AS isAllowInform, a.is_allow_talk AS isAllowTalk, a.state, ui.invited_user_phone AS invitedUserPhone, ui.register_time AS invitedRegisterTime, ui.invest_time AS invitedInvestTime, ui.reward AS invitedreward, u.realname AS invitedRealName, u.nickname AS invitedNickName, u.invest_conditions AS invitedInvestConditions, u.idcard AS invitedIdCard, u.bank_code AS invitedBankCode, u.bank_idcard AS invitedBankIdCard, u.bank_phone AS invitedBankPhone from user_invite ui LEFT JOIN user a on a.user_id = ui.user_id LEFT JOIN user u ON ui.invited_user_id = u.user_id 1=1
AND a.invest_conditions=#{investConditions}
AND a.is_auth=#{isAuth}
AND a.phone=#{phone}
AND a.realname=#{realname}
AND a.idcard=#{idcard}
AND ui.invited_user_phone=#{invitePhone}
AND u.realname=#{inviteRealname}
AND u.idcard=#{inviteIdcard}
order by #{sortBy} #{order}

